import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';
import Axios from "axios";
import React, { useState, useEffect, Component } from 'react';
import { AgGridColumn, AgGridReact } from 'ag-grid-react';
import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-alpine.css';
import BtnCellRenderer from './btn-cell-renderer.jsx';
//modal imports
import { Container } from './Container';
import 'bootstrap/dist/css/bootstrap.min.css';



/*ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);*/

class GridExample extends Component {
  constructor(props) {
    super(props);

    this.state = {
      columnDefs: [
        {
          field: 'id'
        },
        {
          field: 'serie'
        },
        {
          field: 'estatus'
        },
        {
          field: 'establecimiento'
        },
        {
          field: 'vigencia'
        }
        /*{ 
        field: '', 
        cellRenderer: 'btnCellRenderer', 
        cellRendererParams: {
          clicked: function(field) {
            alert(`${field} was clicked`);
          }
        },
        maxWidth: 70
        },
        { 
        field: '', 
        cellRenderer: 'btnCellRenderer', 
        cellRendererParams: {
          clicked: function(field) {
            alert(`${field} was clicked`);
          }
        },
        maxWidth: 50
        }*/
      ],
      defaultColDef: {
        flex: 1,
        minWidth: 60,
      },
      components: {
        btnCellRenderer: BtnCellRenderer
      },
      rowData: [],
    };
  }



  eraseSelectedRowData = () => {

    let selectedNodes = this.gridApi.getSelectedNodes();
    let selectedData = selectedNodes.map(node => node.data);
    var payload =selectedData[0].id;
    console.log(payload);
    Axios.post('http://3.23.104.36:3001/cuponesBorrar',{id:payload})
    .then(res => {
      console.log(res);

    });
  };

  editSelectedRowData = () => {

    let selectedNodes = this.gridApi.getSelectedNodes();
    let selectedData = selectedNodes.map(node => node.data);
    var id =selectedData[0].id;
    var serie =selectedData[0].serie;
    var establecimiento =selectedData[0].establecimiento;
    var estatus =selectedData[0].estatus;
    var vigencia =selectedData[0].vigencia;
    Axios.post('http://3.23.104.36:3001/cuponesEditar',{
      id:id,
      serie:serie,
      establecimiento: establecimiento,
      estatus: estatus,
      vigencia: vigencia
      })
    .then(res => {
      console.log(res);
    });
  };

   onRowSelected = (event) => {
    let selectedNodes = this.gridApi.getSelectedNodes();
    let selectedData = selectedNodes.map(node => node.data);
    var id =selectedData[0].id;
    const formId = id;
    console.log(formId);
  };

  onGridReady = params => {
    this.gridApi = params.api;
    this.gridColumnApi = params.columnApi;

    const updateData = data => {
      this.setState({ rowData: data });
    };

    Axios.post('http://3.23.104.36:3001/cuponesCargar')
    .then(res => {
      console.log(res.data);
      updateData(res.data);
    });

  };


  render() {
    return (
      <div className="ag-theme-alpine" style={{ width: '99vw', height: '90vh' }}>
        <button 
          className = "btn btn-danger btn-sm"
          onClick={this.eraseSelectedRowData}
          style={{margin: 10}}
          >Borrar
        </button>
        <Container triggerText={'Editar'} formId={'this.formId'}/>
        <button 
          className = "btn btn-success btn-sm"
          onClick={this.editSelectedRowData}
          style={{margin: 10}}
          >Agregar
        </button>
        <div
          id="myGrid"
          style={{
            height: '100%',
            width: '100%',
          }}
          className="ag-theme-alpine"
        >
          <AgGridReact
            columnDefs={this.state.columnDefs}
            defaultColDef={this.state.defaultColDef}
            components={this.state.components}
            onGridReady={this.onGridReady}
            rowData={this.state.rowData}
            rowSelection="single"
            onRowSelected={this.onRowSelected.bind(this)}
          />

        </div>
      </div>
    );
  }
}

ReactDOM.render(<GridExample></GridExample>, document.querySelector('#root'));


reportWebVitals();
